CREATE SCHEMA IF NOT EXISTS basicos;

CREATE TABLE basicos."Sexos" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Nombre" character varying NOT NULL,
    CONSTRAINT "PK_Sexos" PRIMARY KEY ("Id")
);

CREATE TABLE basicos."TipoIdentificacions" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Nombre" character varying NOT NULL,
    CONSTRAINT "PK_TipoIdentificacions" PRIMARY KEY ("Id")
);

CREATE TABLE "Personas" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Nombres" character varying NOT NULL,
    "Apellidos" character varying NOT NULL,
    "Direccion" character varying NOT NULL,
    "Telefono" character varying NOT NULL,
    "SexoId" integer NOT NULL,
    "TipoIdentififcacionId" integer NOT NULL,
    "Personas_TipoIdentificacions" integer NOT NULL,
    CONSTRAINT "PK_Personas" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Personas_TipoIdentificacions_Personas_TipoIdentificacions" FOREIGN KEY ("Personas_TipoIdentificacions") REFERENCES basicos."TipoIdentificacions" ("Id") ON DELETE CASCADE,
    CONSTRAINT "Personas_Sexos" FOREIGN KEY ("SexoId") REFERENCES basicos."Sexos" ("Id") ON DELETE CASCADE
);

CREATE INDEX "IX_Personas_Personas_TipoIdentificacions" ON "Personas" ("Personas_TipoIdentificacions");

CREATE INDEX "IX_Personas_SexoId" ON "Personas" ("SexoId");

